<?php $posts=json_decode(file_get_contents(__DIR__.'/posts.json'),true);?><!doctype html><html><head><meta charset='utf-8'><title>PHP Microblog</title><link rel='stylesheet' href='styles.css'></head><body><h1>PHP Microblog</h1><?php foreach($posts as $p): ?><article class='card'><h3><?= htmlspecialchars($p['title']) ?></h3><p><?= htmlspecialchars($p['body']) ?></p></article><?php endforeach; ?></body></html>